import React from "react";
import "./css/style.css";
import { useState } from "react";

function App() {
  let [open, setOpen] = useState(false);
  let variable;

  let modalOnclick = () => {
    if (variable) return;
    setOpen((open = !open));
  };
  return (
    <div className="container">
      <button
        className="open-modal"
        onClick={() => {
          setOpen(true);
        }}
      >
        open-modal
      </button>

      {open && (
        <div className={`modal`} onClick={() => modalOnclick()}>
          <div className="modal-content" onClick={() => (variable = true)}>
            <h3 className="modal__title">Some title</h3>
            <p className="modal__text">Some text</p>
            <div className="modal__block-button">
              <button className="btn modal__button-done">done</button>
              <button className="btn modal__button-exit">exit</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
export default App;
