import React from "react";
import "./css/style.css";
import { useState } from "react";
import Modal from "./components/Modal";


function App() {
  let [open, setOpen] = useState(false);
  return (
    <div className="container">
      <button className="open-modal" onClick={() => {
				setOpen(!open)
			}}>open-modal</button>

			{open && <Modal />}
    </div>
  );
}
export default App;
